mapper.select:
SELECT t.appId AS wxAppid, t.visitPv AS wxVisit, t.visitUv AS wxActive, t.visitUvNew AS wxNew, ROUND(t.stayTimeUv,0) AS wxAvgOnline, DATE(t.refDate) AS wxDate, s.shareUv AS wxShareUser, s.sharePv AS wxShareCount, (r.day1/r.day0) AS wxRemain2 FROM persie_value.wx_daily_visit_trend AS t LEFT JOIN persie_value.wx_daily_summary AS s ON t.appId = s.appId AND t.refDate = s.refDate LEFT JOIN persie_value.wx_daily_retain AS r ON t.appId = r.appId AND t.refDate = r.refDate WHERE DATE(t.refDate) BETWEEN #{start} AND #{end} AND r.dataType ='visit_uv_new'
and t.appId = #{ddAppId, jdbcType=VARCHAR}
GROUP BY t.refDate,t.appId, t.visitPv, t.visitUv, t.visitUvNew, t.stayTimeUv,s.shareUv, s.sharePv,(r.day1/r.day0)
