mapper.select:
select

from config_ad_content
where ddId = #{id, jdbcType=INTEGER}

mapper.update:
update config_ad_content
ddAdType = #{ddAdType, jdbcType=VARCHAR}, ddWeight = #{ddWeight, jdbcType=VARCHAR}, ddImageUrl = #{ddImageUrl, jdbcType=VARCHAR}, ddTargetAppId = #{ddTargetAppId, jdbcType=VARCHAR}, ddTargetAppName = #{ddTargetAppName, jdbcType=VARCHAR}, ddPromoteAppId = #{ddPromoteAppId, jdbcType=VARCHAR}, ddPromoteAppName = #{ddPromoteAppName, jdbcType=VARCHAR}, updateTime = NOW()
where ddId = #{ddId, jdbcType=INTEGER}

delete
from config_ad_content
where ddId = #{ddId, jdbcType=INTEGER}
