mapper.select:
select

from config_ad_space
where ddId = #{id, jdbcType=INTEGER}

mapper.update:
update config_ad_space
ddName = #{ddName, jdbcType=VARCHAR}, ddAdType = #{ddAdType, jdbcType=INTEGER}, ddAllowedOperation = #{ddAllowedOperation, jdbcType=BOOLEAN}, ddWeight = #{ddWeight, jdbcType=INTEGER}, ddStrategyId = #{ddStrategyId, jdbcType=INTEGER}, ddStrategyValue = #{ddStrategyValue, jdbcType=VARCHAR}, ddAdContents = #{ddAdContents, jdbcType=VARCHAR}, updateTime = NOW()
where ddId = #{ddId, jdbcType=INTEGER}

delete
from config_ad_space
where ddId = #{ddId, jdbcType=INTEGER}
