mapper.select:
select 

from user_steal
where userId = #{userid,jdbcType=BIGINT}
  and ftId = #{ftid,jdbcType=INTEGER}

mapper.delete:
delete from user_steal
where userId = #{userid,jdbcType=BIGINT}
  and ftId = #{ftid,jdbcType=INTEGER}

mapper.update:
update user_steal


    stealUserId = #{stealuserid,jdbcType=BIGINT},


    updateTime = #{updatetime,jdbcType=BIGINT},


where userId = #{userid,jdbcType=BIGINT}
  and ftId = #{ftid,jdbcType=INTEGER}

update user_steal
set stealUserId = #{stealuserid,jdbcType=BIGINT},
  updateTime = #{updatetime,jdbcType=BIGINT}
where userId = #{userid,jdbcType=BIGINT}
  and ftId = #{ftid,jdbcType=INTEGER}
