mapper.select:
select
a.id , a.date, a.appId, a.adUnitId, a.adUnitName, a.slotId, w.product_name AS productName, a.clickCount AS clickCount,
ROUND(a.clickRate * 100, 2) AS clickRate, ROUND(a.ecpm, 2) AS ecpm, a.exposureCount AS exposureCount,
ROUND(a.exposureRate* 100, 2) AS exposureRate, a.income AS income, a.reqSuccCount AS reqSuccCount,
ROUND(a.income /clickCount, 2) AS clickIncome
from persie_value.ad_value_wx_adunit a, persie.wx_config w
where DATE(date) between #{beginTime} and #{endTime}
and a.appId = w.ddAppId

    and appId = #{appId, jdbcType=VARCHAR}


    and slotId = #{slotId, jdbcType=VARCHAR}


    and adUnitName = #{adUnitName, jdbcType=VARCHAR}
