mapper.select:
select

from config_ad_position
where ddId = #{id, jdbcType=INTEGER}

mapper.update:
update config_ad_position
ddName = #{ddName, jdbcType=VARCHAR}, ddAdTypes = #{ddAdTypes, jdbcType=VARCHAR}, ddAllowedOperation = #{ddAllowedOperation, jdbcType=BOOLEAN}, ddAdSpaces = #{ddAdSpaces, jdbcType=VARCHAR}, ddStrategyId = #{ddStrategyId, jdbcType=INTEGER}, ddStrategyValue = #{ddStrategyValue, jdbcType=VARCHAR}, ddShowWxAd = #{ddShowWxAd, jdbcType=BOOLEAN}, ddShowWxReVideoAd = #{ddShowWxReVideoAd, jdbcType=BOOLEAN}, updateTime = NOW()
where ddId = #{ddId, jdbcType=INTEGER}
