mapper.select:
select 

from config_subscribe
where ddAppId = #{ddAppId,jdbcType=VARCHAR}
  and ddId = #{ddId,jdbcType=VARCHAR}

mapper.delete:
delete from config_subscribe
where ddAppId = #{ddAppId,jdbcType=VARCHAR}
  and ddId = #{ddId,jdbcType=VARCHAR}

mapper.insert:
insert into config_subscribe (ddAppId, ddId, ddNo, 
  ddType, ddTitle, ddExplain, 
  ddStatus, ddRule, times
  )
values (#{ddAppId,jdbcType=VARCHAR}, #{ddId,jdbcType=VARCHAR}, #{ddNo,jdbcType=INTEGER}, 
  #{ddType,jdbcType=VARCHAR}, #{ddTitle,jdbcType=VARCHAR}, #{ddExplain,jdbcType=VARCHAR}, 
  #{ddStatus,jdbcType=BIT}, #{ddRule,jdbcType=VARCHAR}, #{times,jdbcType=TIMESTAMP}
  )

mapper.update:
update config_subscribe
set ddNo = #{ddNo,jdbcType=INTEGER},
  ddType = #{ddType,jdbcType=VARCHAR},
  ddTitle = #{ddTitle,jdbcType=VARCHAR},
  ddExplain = #{ddExplain,jdbcType=VARCHAR},
  ddStatus = #{ddStatus,jdbcType=BIT},
  ddRule = #{ddRule,jdbcType=VARCHAR},
  times = #{times,jdbcType=TIMESTAMP}
where ddAppId = #{ddAppId,jdbcType=VARCHAR}
  and ddId = #{ddId,jdbcType=VARCHAR}
