mapper.select:
select ddId, ddName, ddAdUnit, ddAllowedShow, ddTime, ddStrategyId, ddStrategyValue
from config_ad_wx
where ddId = #{id, jdbcType=INTEGER}

mapper.update:
update config_ad_wx
ddName = #{ddName, jdbcType=VARCHAR}, ddAdUnit = #{ddAdUnit, jdbcType=VARCHAR}, ddAllowedShow = #{ddAllowedShow, jdbcType=BOOLEAN}, ddTime = #{ddTime, jdbcType=INTEGER}, ddStrategyId = #{ddStrategyId, jdbcType=INTEGER}, ddStrategyValue = #{ddStrategyValue, jdbcType=VARCHAR}, updateTime = NOW()
where ddId = #{ddId, jdbcType=INTEGER}

delete
from config_ad_wx
where ddId = #{ddId, jdbcType=INTEGER}
